                            <div className={styles.barTrack}>
                                <div className={styles.barFill} style={{ width: '40%', background: '#3b82f6' }}></div>
                            </div>
                        </div>
                        <div className={styles.budgetRow}>
                            <span>Stay</span>
                            <div className={styles.barTrack}>
                                <div className={styles.barFill} style={{ width: '35%', background: '#10b981' }}></div>
                            </div>
                        </div>
