            await fetch(`${API_BASE_URL}/api/admin/destination-requests/${id}/approve`, { method: 'POST', headers: getHeaders() });
            fetchDestRequests();
            fetchStats();
        } catch (e) { console.error(e); }
    };

    const handleRejectRequest = async (id) => {
