        { id: 'destinations', icon: MapPin, label: 'Destinations' },
        { id: 'blogs', icon: FileText, label: 'Content' },
        { id: 'users', icon: Users, label: 'Users & Trips' },
        { id: 'requests', icon: Inbox, label: 'Requests', badge: destRequests.length },
